! function (e) {
    e("html").removeClass("no-js"), e("header a").click(function (t) {
        if (!e(this).hasClass("no-scroll")) {
            t.preventDefault();
            var i = e(this).attr("href"),
                n = e(i).offset().top;
            e("html, body").animate({
                scrollTop: n + "px"
            }, Math.abs(window.pageYOffset - e(i).offset().top) / 1), e("header").hasClass("active") && e("header, body").removeClass("active")
        }
    }), e("#to-top").click(function () {
        e("html, body").animate({
            scrollTop: 0
        }, 500)
    }), e("#lead-down span").click(function () {
        var t = e("#lead").next().offset().top;
        e("html, body").animate({
            scrollTop: t + "px"
        }, 500)
    }), e(".show-more").click(function() {
        if(e(this).attr("data-more") == 0) {
            e(this).attr("data-more", 1);
            e(".long-text").css("display", "inline");
            e(this).text("");
        }

        
    }), e("#experience-timeline").each(function () {
        $this = e(this), $userContent = $this.children("div"), $userContent.each(function () {
            e(this).addClass("vtimeline-content").wrap('<div class="vtimeline-point"><div class="vtimeline-block"></div></div>')
        }), $this.find(".vtimeline-point").each(function () {
            e(this).prepend('<div class="vtimeline-icon"><i class="fa fa-map-marker"></i></div>')
        }), $this.find(".vtimeline-content").each(function () {
            var t = e(this).data("date");
            t && e(this).parent().prepend('<span class="vtimeline-date">' + t + "</span>")
        })
    }), e("#mobile-menu-open").click(function () {
        e("header, body").addClass("active")
    }), e("#mobile-menu-close").click(function () {
        e("header, body").removeClass("active")
    }), e("#view-more-projects").click(function (t) {
        t.preventDefault(), e(this).fadeOut(300, function () {
            e("#more-projects").fadeIn(300)
        })
    })
}(jQuery);

window.onscroll = function() {
    myFunction();
}

function myFunction() {
    var scroll = document.body.scrollTop || document.documentElement.scrollTop;
    var height = document.documentElement.scrollHeight - document.documentElement.clientHeight;
    var scrolled = (scroll / height) * 100;
    document.getElementById("myBar").style.width = scrolled + "%";
}

var TxtType = function(el, toRotate, period) {
    this.toRotate = toRotate;
    this.el = el;
    this.loopNum = 0;
    this.period = parseInt(period, 10) || 2000;
    this.txt = '';
    this.tick();
    this.isDeleting = false;
};

TxtType.prototype.tick = function() {
    var i = this.loopNum % this.toRotate.length;
    var fullTxt = this.toRotate[i];

    if (this.isDeleting) {
    this.txt = fullTxt.substring(0, this.txt.length - 1);
    } else {
    this.txt = fullTxt.substring(0, this.txt.length + 1);
    }

    this.el.innerHTML = '<span class="wrap">'+this.txt+'</span>';

    var that = this;
    var delta = 200 - Math.random() * 100;

    if (this.isDeleting) { delta /= 2; }

    if (!this.isDeleting && this.txt === fullTxt) {
    delta = this.period;
    this.isDeleting = true;
    } else if (this.isDeleting && this.txt === '') {
    this.isDeleting = false;
    this.loopNum++;
    delta = 500;
    }

    setTimeout(function() {
    that.tick();
    }, delta);
};

window.onload = function() {
    var elements = document.getElementsByClassName('typewrite');
    for (var i=0; i<elements.length; i++) {
        var toRotate = elements[i].getAttribute('data-type');
        var period = elements[i].getAttribute('data-period');
        if (toRotate) {
          new TxtType(elements[i], JSON.parse(toRotate), period);
        }
    }
    var css = document.createElement("style");
        css.type = "text/css";
        css.innerHTML = ".typewrite > .wrap { border-right: 0.08em solid #fff}";
        document.body.appendChild(css);
};